mapper.select:
SELECT id, category, title, hit, REGDATE, writer, name FROM( SELECT ROWNUM AS RNUM, b.* FROM ( SELECT nb.id, category, title, hit, nb.REGDATE, writer, name FROM NOTICE_BOARD nb, MEMBER m WHERE nb.writer = m.id
AND category = #{category}
ORDER BY nb.id desc
) b WHERE ROWNUM <= #{last_board} ) board WHERE #{start_board} <= RNUM

mapper.update:
 	UPDATE NOTICE_BOARD
	SET hit = hit + 1
	WHERE id = #{id}
